mapper.delete:
DELETE FROM multiple_practise
WHERE
	practise_id = #{practise_id}
AND
	question_id = #{question_id}

mapper.update:
UPDATE
	multiple_practise
SET
	stuanswer = #{answer }
WHERE
	practise_id = #{practise_id }
AND
	question_id = #{question_id }

UPDATE
	multiple_practise
SET
	status = #{status }
WHERE
	practise_id = #{practise_id }
AND
	question_id = #{question_id }
